    private void CaixaCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_CaixaCalcularActionPerformed
        Peso=Float.valueOf(CaixaPeso.getText());
        Altura=Float.valueOf(CaixaAltura.getText());
        IMC= Peso/(Altura*Altura);
        CaixaResultado.setText(verificaIndice(IMC));
        
    }//GEN-LAST:event_CaixaCalcularActionPerformed

