    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
    n1=Integer.valueOf(campo1.getText());
    
    if(n1>10){        
        resultado.setText("Número é maior que 10");
    }
    
    else if(n1<=10){        
        resultado.setText("Número é menor ou igual a 10");
    }
    
    else{            
    resultado.setText("Error not found!");
    }
    
    }//GEN-LAST:event_jButton1ActionPerformed

